sp_widget.client_script:
function($scope, $http, spModal) {
  var c = this;

  c.approveVenue = function(sys_id) {
    spModal.open({
      title: 'Approve Venue Bookings',
      message: 'Do you want to open this venue booking record?',
      buttons: [
        { label: 'Approve', primary: true },
        { label: 'Deny' }
      ]
    }).then(function(result) {
      if (result.button.label === 'Approve') {
        window.location.href = '/x_1791009_event_ma_venue.do?sys_id=' + sys_id;
      }
    });
  };

  c.denyVenue = function(sys_id) {
    if (!confirm("Are you sure you want to deny this booking?")) return;

    $http.delete('/api/now/table/x_1791009_event_ma_feedback/' + sys_id)
      .then(function(response) {
        alert('Venue Booking Denied.');
        $scope.server.get(); // Refresh the widget
      }, function(error) {
        alert('Failed to deny booking.');
      });
  };
}

sp_widget.script:
(function() {

  data.venueList = [];

  var gr = new GlideRecord('x_1791009_event_ma_venue'); // Replace with your table name

  gr.orderByDesc('sys_created_on');

  gr.query();

  while (gr.next()) {

    data.venueList.push({

      sys_id: gr.getUniqueValue(),

      location: gr.getValue('location'),

      contact_person: gr.getValue('contact_person'),

      capacity: gr.getDisplayValue('capacity'),

      venue_name: gr.getDisplayValue('venue_name'),

      created: gr.getDisplayValue('sys_created_on')

    });

  }

})();
