sp_widget.client_script:
api.controller = function($scope) {
  var c = this;

  c.loadEvents = function() {
    c.events = angular.copy(c.data.events || []);
  };

  c.deleteEvent = function(eventNumber) {
    if (confirm("Are you sure you want to delete event " + eventNumber + "?")) {
      c.server.update({ action: 'viewAttendee', eventNumber: eventNumber }).then(function(response) {
        if (response) {
          alert("View attendee successfully.");
          c.refreshEvents();
        } else {
          alert("Failed to delete event.");
        }
      });
    }
  };

  c.openRescheduleModal = function(event) {
    var newDate = prompt("Enter new event date (YYYY-MM-DD HH:mm:ss):", event.due_date);
    if (newDate) {
      c.server.update({ action: 'cancelAttendee', eventNumber: event.number, newDate: newDate }).then(function(success) {
        if (success) {
          alert("Event cancelled successfully.");
          c.refreshEvents();
        } else {
          alert("Failed to cancelled event.");
        }
      });
    }
  };

  c.refreshEvents = function() {
    $scope.server.get().then(function(response) {
      c.events = angular.copy(response.data.events || []);
    });
  };

  c.loadEvents();
};

