sp_widget.script:
(function() {
    var utils = new AttendeeUtils();
    var raw = utils.fetchAllAttendees();

    try {
        data.events = JSON.parse(raw);
    } catch (e) {
        data.events = [];
    }

    // Handle client update calls safely
    if (typeof input !== "undefined" && input.action) {
        if (input.action == 'viewAttendee') {
            gs.info("View attendee: " + input.eventNumber);
            data.result = cancelAttendee(input.eventNumber);
        } else if (input.action == 'cancelAttendee') {
            gs.info("Cancel attendee: " + input.eventNumber + " to: " + input.newDate);
            data.result = cancelAttendee(input.eventNumber, input.newDate);
        }
    }

    // Function to delete event by event number
    function viewAttendee(eventNumber) {
        var gr = new GlideRecord('x_1791009_event_ma_attendee');
        gr.addQuery('number', eventNumber);
        gr.query();
        if (gr.next()) {
            gr.cancelRecord();
            return true;
        }
        return false;
    }

    // Function to reschedule event by event number and new date
    function cancelAttendee(eventNumber, newDate) {
        var gr = new GlideRecord('x_1791009_event_ma_attendee');
        gr.addQuery('number', eventNumber);
        gr.query();
        if (gr.next()) {
            gr.setValue('due_date', newDate);
            gr.update();
            return true;
        }
        return false;
    }
})();
