sp_widget.client_script:
function($scope, $http, spModal) {
  var c = this;

  c.viewFeedback = function(sys_id) {
    spModal.open({
      title: 'View Feedback',
      message: 'Do you want to open this feedback record?',
      buttons: [
        { label: 'View', primary: true },
        { label: 'Cancel' }
      ]
    }).then(function(result) {
      if (result.button.label === 'View') {
        window.location.href = '/x_1791009_event_ma_feedback.do?sys_id=' + sys_id;
      }
    });
  };

  c.deleteFeedback = function(sys_id) {
    if (!confirm("Are you sure you want to delete this feedback?")) return;

    $http.delete('/api/now/table/x_1791009_event_ma_feedback/' + sys_id)
      .then(function(response) {
        alert('Feedback deleted.');
        $scope.server.get(); // Refresh the widget
      }, function(error) {
        alert('Failed to delete feedback.');
      });
  };
}

sp_widget.script:
(function() {
  data.feedbackList = [];

  var gr = new GlideRecord('x_1791009_event_ma_feedback'); // Replace with your table name
  gr.orderByDesc('sys_created_on');
  gr.query();

  while (gr.next()) {
    data.feedbackList.push({
      sys_id: gr.getUniqueValue(),
      rating: gr.getValue('rating'),
      comments: gr.getValue('comments'),
      attendee: gr.getDisplayValue('attendee'),
      event: gr.getDisplayValue('event_id'),
      created: gr.getDisplayValue('sys_created_on')
    });
  }
})()
