sp_widget.script:
(function() {
  data.upcomingEvents = [];
  var gr = new GlideRecord('x_1791009_event_ma_event_record');
  gr.addQuery('event_date', '>=', gs.nowDateTime());
  gr.orderBy('event_date');
  gr.setLimit(5);
  gr.query();
  while (gr.next()) {
    data.upcomingEvents.push({
      name: gr.getValue('event_name'),
      date: gr.getDisplayValue('event_date'),
      location: gr.getValue('location'),
      status: gr.getDisplayValue('status')
    });
  }

  // Add similar logic for tasks and notifications
})();
