sys_script_client.script:
function onSubmit() {
  // Get the values from the form
  var startTime = g_form.getValue('start_time');
  var endTime   = g_form.getValue('end_time');

  // Only validate if both fields are filled
  if (startTime && endTime) {
    var startDate = new Date(startTime);
    var endDate   = new Date(endTime);

    if (endDate < startDate) {
      // Display an inline error on the end_time field
      g_form.showFieldMsg('end_time',
        'End time cannot be before start time.',
        'error');
      return false; // Prevent form submission
    }
  }
  return true; // Allow submission if validation passes
}
