sys_script_include.script:
var SubmitFeedbackUtils = Class.create();
SubmitFeedbackUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    fetchDetails: function() {
          var sysid = this.getParameter('sysparm_sys_id'); 
        var feedback = new GlideRecord('x_1791009_event_ma_feedback');

        if (feedback.get(sysid)) {
            var newfeedback = new GlideRecord('x_1791009_event_ma_feedback');
           newfeedback.initialize();
           newfeedback.event_id =feedback.event_id; 
           newfeedback.rating =feedback.rating;
           newfeedback.insert();
            return 'true';
        }

        return 'false'; 
    },

    type: 'SubmitFeedbackUtils'
});
