sys_script_include.script:
var BudgetUtils = Class.create();
BudgetUtils.prototype = {
    initialize: function() {},

    fetchAllBudgets: function() {
        var res = [];
        var gr = new GlideRecord('x_1791009_event_ma_budget'); // Use your actual budget table name
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                total_budget: gr.getValue('total_budget'),
                amount_spent: gr.getValue('amount_spent'),
                amount_remaining: gr.getValue('amount_remaining'),
                status: gr.getDisplayValue('status')
            });
        }
        return JSON.stringify(res);
    },

    TotalBudget: function(id) {
        var event = new GlideRecord('x_1791009_event_ma_budget');
        event.addQuery('number', id);
        event.query();
        while (event.next()) {
            event.total_budget = event.getValue('total_budget');

            event.update();
        }
    },
    remainingBudget: function(id) {
        var remaining = 0;
        var event = new GlideRecord('x_1791009_event_ma_budget');
        event.addQuery('number', id);
        event.query();
        if (event.next()) {
            remaining = event.getValue('amount_remaining');
        }
        return remaining;
    },
    isApproved: function(id) {
        var budget = new GlideRecord('x_1791009_event_ma_budget');
        budget.addQuery('number', id);
        budget.query();
        while (budget.next()) {
            if (budget.getValue('status') == 'approved') {
                return 'Approved';
            }
        }
        return false;
    },
    getEventBudget: function(eventid) {
        var res = [];
        var budget = new GlideRecord('x_1791009_event_ma_budget');
        budget.addQuery('event_id.number', eventid);
        budget.query();
        if (budget.next()) {
            res.push({
                id: budget.getDisplayValue(),
                totalBudget: budget.getValue('total_budget'),
            });
        }
        return JSON.stringify(res);

    },

    type: 'BudgetUtils'
};
