var EventDate = Class.create();
EventDate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
  reschedule:function(){
	var sysId = this.getParameter('sysparm_sys_id');
        var newDate = this.getParameter('sysparm_new_date');
        var eventGR = new GlideRecord('x_1791009_event_ma_event_record'); 
        if (eventGR.get(sysId)) {
            eventGR.setValue('event_date', newDate);
            eventGR.update();
                gs.eventQueue('x_1791009_event_ma.cancel.event', eventGR, null, newDate); 
            }

            return 'success';
        } ,
		cancelTask: function() {
      var sysId = this.getParameter('sysparm_sys_id');

        var eventGR = new GlideRecord('x_1791009_event_ma_event_record');
        if (eventGR.get(sysId)) {
            eventGR.u_status = 'cancelled'
            eventGR.update();
            // gs.eventQueue('event.cancelled', eventGR, eventGR.getValue('assigned_to'), eventGR.short_description);

            return 'success';
        }

        return 'Record not found';

    },
    type: 'EventDate'
});
