sys_script_include.script:
var EventUtilsClient = Class.create();
EventUtilsClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    fetchEventDetails: function() {
        var eventId = this.getParameter('sysparm_event_id');
        var grEvent = new GlideRecord('x_1791009_event_ma_event_record');
        if (grEvent.get(eventId)) {
            return JSON.stringify({
                name: grEvent.getDisplayValue('event_name'),
                date: grEvent.getDisplayValue('event_date'),
                status: grEvent.getDisplayValue('status')
            });
        }
        return '';
    },
    type: 'EventUtilsClient'
});
