sys_script_include.script:
var CreateAttendee = Class.create();
CreateAttendee.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    fetchDetails: function() {
          var sysid = this.getParameter('sysparm_sys_id'); 
        var attend = new GlideRecord('x_1791009_event_ma_attendee');

        if (attend.get(sysid)) {
            var newAttendee = new GlideRecord('	x_1791009_event_ma_attendee');
            newAttendee.initialize();
            newAttendee.first_name = attend.first_name;
            newAttendee.last_name = attend.last_name;
            newAttendee.email = attend.email;
            newAttendee.contact_number = attend.contact_number;
            newAttendee.event_id = attend.event_id; 
            newAttendee.insert();
            return 'true';
        }

        return 'false'; // explicit return on failure
    },
    type: 'CreateAttendee'
});
