sys_script_include.script:
var VendorUtils = Class.create();
VendorUtils.prototype = {
    initialize: function() {},

    fetchAllVendors: function() {
        var res = [];
        var gr = new GlideRecord('x_1791009_event_ma_vendor'); // Use your actual vendor table name
        gr.query();
        while (gr.next()) {
            res.push({
                number: gr.getValue('number'),
                vendor_name: gr.getValue('vendor_name'),
                contact_person: gr.getValue('contact_person'),
                contact_number: gr.getValue('contact_number'),
                //available: gr.getDisplayValue('available')
            });
        }
        return JSON.stringify(res);
    },

    // 1. Fetch vendor details by vendor sys_id
    getVendorDetails: function(vendorId) {
        var gr = new GlideRecord('x_1791009_event_ma_vendor');
        if (gr.get(vendorId)) {
            return {
                name: gr.getValue('vendor_name'),
                contact: gr.getValue('contact_number'),
                service_type: gr.getValue('email'),
                //is_available: gr.getValue('available')
            };
        }
        return null;
    },

    // 2. Check if vendor is available (assumes boolean field u_is_available)
    isVendorAvailable: function(vendorId) {
        var gr = new GlideRecord('x_1791009_event_ma_vendor');
        if (gr.get(vendorId)) {
            return gr.getValue('available') == 'true';
        }
        return false;
    },

    // 3. Fetch vendors by event ID (if u_event reference field exists)
    getVendorsByEvent: function(eventId) {
        var list = [];
        var gr = new GlideRecord('x_1791009_event_ma_vendor');
        gr.addQuery('event_id.number', eventId); // assumes u_event is a reference to event_record
        gr.query();

        while (gr.next()) {
            list.push({
                name: gr.getValue('vendor_name'),
                service_type: gr.getValue('contact_person'),
                contact: gr.getValue('contact_number'),
                is_available: gr.getValue('available')
            });
        }

        return list;
    },

    type: 'VendorUtils'
};
