sys_script_include.script:
var AttendeeEventUtils = Class.create();
AttendeeEventUtils.prototype = Object.extendsObject(AbstractAjaxProcessor, {

  getEventDetails: function() {
    var eventId = this.getParameter('sysparm_event_id');
    if (!eventId) {
      return JSON.stringify({ error: true, message: 'Missing event ID' });
    }

    var gr = new GlideRecord('event_record');
    if (!gr.get(eventId)) {
      return JSON.stringify({ error: true, message: 'Event not found' });
    }

    return JSON.stringify({
      name: gr.getValue('event_name'),
      start: gr.getValue('start_date'),
      location: gr.getValue('location')
    });
  },

  type: 'AttendeeEventUtils'
});
