sys_script_include.script:
var EventUtilsClient = Class.create();
EventUtilsClient.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getDetails:function()
	{    id = this.getParameter('sysparm_sysid');
	var res = [];
		var event = new GlideRecord('x_1791009_event_ma_event_record');
		event.addQuery('event_id',id);
		event.query();
		while(event.next())
        {
			res.push({
				name : event.getValue('event_name'),
				date: event.getValue('event_date'),
				location:event.getValue('location'),
			});
		}
      return JSON.stringify(res);
	},
	isVIP:function(){
      id = this.getParameter('sysparm_sysid');
		var event = new GlideRecord('x_1791009_event_ma_event_record');
		event.addQuery('event_id',id);
		event.query();
		if(event.next())
		{
			if(event.vip)
			return true;
		}
		return false;
	},

    type: 'EventUtilsClient'
});
