sysauto_script.script:
(function executeRule() {
        var today = new GlideDateTime();
        var next3Days = new GlideDateTime();
        next3Days.addDaysUTC(3);

        var taskGR = new GlideRecord('x_1791009_event_ma_event_task');
        taskGR.addQuery('due_date', '>=', today);
        taskGR.addQuery('due_date', '<=', next3Days);
        taskGR.addQuery('status', '!=', 'Completed'); // Assuming 3 = Completed
        taskGR.query();

        while (taskGR.next()) {
            var event = taskGR.event_id.getRefRecord();
			if (!event || !event.event_planner) continue;

    var planner = event.event_planner;
    if (!planner.email) continue;

    var subject = 'ðŸ”” Upcoming Task Reminder: ' + taskGR.task_name;
    var body = 
      'Hello ' + planner.name + ',\n\n' +
      'You have a task due soon:\n\n' +
      'Event: ' + event.event_name + '\n' +
      'Task: ' + taskGR.task_name + '\n' +
      'Due Date: ' + taskGR.due_date.getDisplayValue() + '\n\n' +
      'Please take action if needed.\n\n' +
      '-- Event Management System';

    gs.email(planner.email, '', subject, body);
    gs.info('[Reminder Sent] ' + planner.email + ' â†’ ' + taskGR.short_description);
  }
})();
