sysauto_script.script:
(function executeRule() {
var eventSysId = '18e8f517c3072210f23870edd40131e0';

var taskGR = new GlideRecord('x_1791009_event_ma_event_task');
taskGR.addQuery('event_id', eventSysId); // Filter tasks by event ID
taskGR.query();

if (!taskGR.hasNext()) {
    gs.info("No tasks found for the given event.");
} else {
    while (taskGR.next()) {
        var taskName = taskGR.getValue('task_name') || "N/A"; 
        var assignedTo = taskGR.getDisplayValue('assigned_to') || "Unassigned";
        var dueDate = taskGR.getValue('due_date') ? taskGR.getValue('due_date').toString() : "No Due Date"; 
        var status = taskGR.getDisplayValue('status') || "No Status";

        gs.info("Task Name: " + taskName +
                " | Assigned To: " + assignedTo +
                " | Due Date: " + dueDate +
                " | Status: " + status);
    }
}
})();
